import React, { useEffect, useState } from "react";
import QRCode from "qrcode.react";

export default function KioskHome({ kioskName, onLogout }) {
  const [qrValue, setQrValue] = useState("");

  useEffect(() => {
    // Example: You can generate a QR that links user to this kiosk‚Äôs connection URL
    // Replace localhost:4000 with your actual backend domain or LAN IP
    const kioskLink = `http://localhost:4000/connect?kiosk=${encodeURIComponent(
      kioskName
    )}`;
    setQrValue(kioskLink);
  }, [kioskName]);

  return (
    <div style={styles.container}>
      <div style={styles.card}>
        <h1 style={styles.title}>üñ®Ô∏è Kiosk Dashboard</h1>
        <h2 style={styles.subtitle}>Welcome, {kioskName}!</h2>
        <p style={styles.info}>
          Ask the user to scan this QR code using their phone or computer camera
          from the User App to connect and send files for printing.
        </p>

        <div style={styles.qrContainer}>
          {qrValue ? (
            <QRCode value={qrValue} size={220} bgColor="#ffffff" fgColor="#333333" />
          ) : (
            <p>Generating QR code...</p>
          )}
        </div>

        <p style={styles.linkText}>
          üì± QR Code Link:{" "}
          <span style={{ color: "#0078FF" }}>{qrValue}</span>
        </p>

        <button style={styles.logoutBtn} onClick={onLogout}>
          üö™ Logout
        </button>
      </div>
    </div>
  );
}

const styles = {
  container: {
    height: "100vh",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    background: "linear-gradient(135deg, #2B32B2, #1488CC)",
    fontFamily: "Segoe UI, Roboto, sans-serif",
  },
  card: {
    backgroundColor: "white",
    padding: "40px 60px",
    borderRadius: "20px",
    boxShadow: "0 8px 25px rgba(0,0,0,0.3)",
    textAlign: "center",
    width: "480px",
  },
  title: {
    fontSize: "2.4rem",
    color: "#333",
    marginBottom: "10px",
  },
  subtitle: {
    fontSize: "1.5rem",
    color: "#444",
    marginBottom: "20px",
  },
  info: {
    fontSize: "1rem",
    color: "#555",
    marginBottom: "25px",
  },
  qrContainer: {
    backgroundColor: "#f9f9f9",
    padding: "20px",
    borderRadius: "15px",
    display: "inline-block",
    boxShadow: "0 4px 10px rgba(0,0,0,0.1)",
    marginBottom: "20px",
  },
  linkText: {
    fontSize: "0.9rem",
    color: "#666",
    marginTop: "10px",
    marginBottom: "30px",
  },
  logoutBtn: {
    backgroundColor: "#f44336",
    color: "white",
    border: "none",
    padding: "12px 25px",
    fontSize: "1rem",
    borderRadius: "10px",
    cursor: "pointer",
  },
};
