import React from 'react';

export default function UserHome({ onLogout, onScan }) {
  return (
    <div style={styles.container}>
      <div style={styles.card}>
        <h1 style={styles.title}>üì± User Dashboard</h1>
        <p style={styles.subtitle}>
          Welcome! To start printing, please scan the QR code shown on your kiosk machine.
        </p>

        <div style={styles.actions}>
          <button style={styles.scanBtn} onClick={onScan}>
            üîç Scan Kiosk QR
          </button>
          <button style={styles.logoutBtn} onClick={onLogout}>
            üö™ Logout
          </button>
        </div>

        <p style={styles.note}>Make sure your camera permission is enabled when scanning.</p>
      </div>
    </div>
  );
}

const styles = {
  container: {
    height: '100vh',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    background: 'linear-gradient(135deg, #74ABE2, #5563DE)',
    fontFamily: 'Segoe UI, Roboto, sans-serif',
  },
  card: {
    backgroundColor: 'white',
    padding: '50px 60px',
    borderRadius: '20px',
    boxShadow: '0 8px 20px rgba(0,0,0,0.2)',
    textAlign: 'center',
    width: '400px',
  },
  title: {
    fontSize: '2rem',
    color: '#333',
    marginBottom: '15px',
  },
  subtitle: {
    fontSize: '1.1rem',
    color: '#555',
    marginBottom: '30px',
  },
  actions: {
    display: 'flex',
    flexDirection: 'column',
    gap: '20px',
  },
  scanBtn: {
    backgroundColor: '#4CAF50',
    color: 'white',
    border: 'none',
    padding: '15px 20px',
    fontSize: '1.2rem',
    borderRadius: '10px',
    cursor: 'pointer',
    transition: 'transform 0.2s ease',
  },
  logoutBtn: {
    backgroundColor: '#f44336',
    color: 'white',
    border: 'none',
    padding: '12px 20px',
    fontSize: '1.1rem',
    borderRadius: '10px',
    cursor: 'pointer',
  },
  note: {
    marginTop: '25px',
    fontSize: '0.9rem',
    color: '#777',
  },
};
