import React, { useState, useEffect } from "react";
import { io } from "socket.io-client";


export default function UserUpload() {
  const [kioskId, setKioskId] = useState(null);
  const [file, setFile] = useState(null);
  const [msg, setMsg] = useState("");

  // üîç Extract kioskId from URL query param
  useEffect(() => {
    const params = new URLSearchParams(window.location.search);
    const id = params.get("kioskId");
    if (id) 
    {
        setKioskId(id);
        const socket = io("http://localhost:4000");
        socket.emit("userConnected", id);
      return () => socket.disconnect();
    }
  }, []);

  const handleUpload = async (e) => {
    e.preventDefault();
    if (!file || !kioskId) {
      setMsg("Please select a file and ensure kiosk is connected.");
      return;
    }

    const formData = new FormData();
    formData.append("file", file);
    formData.append("kioskId", kioskId);

    try {
      const res = await fetch("http://localhost:4000/api/upload", {
        method: "POST",
        body: formData,
      });

      const data = await res.json();
      if (data.success) {
        setMsg("‚úÖ File uploaded successfully. Printing started!");
      } else {
        setMsg("‚ö†Ô∏è Failed to upload file.");
      }
    } catch (err) {
      console.error(err);
      setMsg("‚ùå Error connecting to server.");
    }
  };

  return (
    <div style={styles.container}>
      <div style={styles.card}>
        <h1 style={styles.title}>üì§ Upload File for Printing</h1>
        {kioskId ? (
          <p style={styles.subtitle}>
            Connected to Kiosk: <strong>{kioskId}</strong>
          </p>
        ) : (
          <p style={{ color: "red" }}>No kiosk connection found.</p>
        )}

        <form onSubmit={handleUpload} style={styles.form}>
          <input
            type="file"
            accept="application/pdf,image/*"
            onChange={(e) => setFile(e.target.files[0])}
            style={styles.fileInput}
          />
          <button type="submit" style={styles.button}>
            Upload & Print
          </button>
        </form>

        {msg && <p style={styles.message}>{msg}</p>}
      </div>
    </div>
  );
}

const styles = {
  container: {
    height: "100vh",
    background: "linear-gradient(135deg, #2193b0, #6dd5ed)",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    fontFamily: "Segoe UI, Roboto, sans-serif",
  },
  card: {
    background: "white",
    padding: "40px 60px",
    borderRadius: "20px",
    textAlign: "center",
    width: "460px",
    boxShadow: "0 10px 25px rgba(0,0,0,0.3)",
  },
  title: { fontSize: "1.8rem", marginBottom: "10px", color: "#333" },
  subtitle: { marginBottom: "25px", color: "#444" },
  form: { display: "flex", flexDirection: "column", gap: "15px" },
  fileInput: { fontSize: "1rem" },
  button: {
    backgroundColor: "#0078ff",
    color: "white",
    border: "none",
    padding: "12px",
    borderRadius: "10px",
    cursor: "pointer",
    fontSize: "1rem",
  },
  message: {
    marginTop: "15px",
    fontWeight: "500",
  },
};
