import React, {useState} from 'react';

export default function Login({type,onBack}){
  const [form, setForm] = useState({});
  const [msg, setMsg] = useState(null);

  const handleSubmit = async (e)=>{
    e.preventDefault();
    const body = type==='user'?{type:'user',username:form.username,password:form.password}:{type:'kiosk',kiosk_name:form.kiosk_name,password:form.password};
    const res = await fetch('http://localhost:4000/api/login',{method:'POST',headers:{'Content-Type':'application/json'},body:JSON.stringify(body)});
    const data = await res.json();
    setMsg(JSON.stringify(data,null,2));
  };

  return (
    <div>
      <button onClick={onBack}>Back</button>
      <h2>{type==='user'?'User Login':'Kiosk Login'}</h2>
      <form onSubmit={handleSubmit}>
        {type==='user'?<input placeholder='Username' value={form.username||''} onChange={e=>setForm({...form,username:e.target.value})}/>
          :<input placeholder='Kiosk Name' value={form.kiosk_name||''} onChange={e=>setForm({...form,kiosk_name:e.target.value})}/>}
        <br/>
        <input type='password' placeholder='Password' value={form.password||''} onChange={e=>setForm({...form,password:e.target.value})}/>
        <br/><button>Login</button>
      </form>
      {msg && <pre>{msg}</pre>}
    </div>
  );
}
