import React from "react";
import { QRCodeCanvas } from "qrcode.react";

export default function KioskHome({ kioskId = "KIOSK12345", onLogout }) {
  return (
    <div style={styles.container}>
      <div style={styles.card}>
        <h1 style={styles.title}>üñ®Ô∏è Printing Kiosk</h1>
        <h3 style={styles.subTitle}>Welcome to {kioskId}</h3>

        <p style={styles.text}>
          üì± Scan the QR code below using your mobile device to connect and send files for printing.
        </p>

        <div style={styles.qrBox}>
          <QRCodeCanvas value={kioskId} size={230} includeMargin={true} />
        </div>

        <p style={styles.kioskId}>Kiosk ID: <strong>{kioskId}</strong></p>

        {onLogout && (
          <button style={styles.logoutBtn} onClick={onLogout}>
            üö™ Logout
          </button>
        )}
      </div>
    </div>
  );
}

const styles = {
  container: {
    height: "100vh",
    width: "100vw",
    background: "linear-gradient(135deg, #2B32B2, #1488CC)",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    fontFamily: "Segoe UI, Roboto, sans-serif",
  },
  card: {
    backgroundColor: "white",
    padding: "40px 60px",
    borderRadius: "20px",
    boxShadow: "0 10px 25px rgba(0,0,0,0.3)",
    textAlign: "center",
    width: "460px",
  },
  title: {
    fontSize: "2rem",
    color: "#222",
    marginBottom: "10px",
  },
  subTitle: {
    fontSize: "1.3rem",
    color: "#444",
    marginBottom: "20px",
  },
  text: {
    color: "#555",
    fontSize: "1rem",
    marginBottom: "25px",
  },
  qrBox: {
    backgroundColor: "#f7f9fc",
    borderRadius: "16px",
    padding: "20px",
    display: "inline-block",
    boxShadow: "0 4px 10px rgba(0,0,0,0.1)",
  },
  kioskId: {
    marginTop: "20px",
    color: "#555",
  },
  logoutBtn: {
    marginTop: "25px",
    background: "#f44336",
    color: "white",
    border: "none",
    padding: "12px 30px",
    borderRadius: "10px",
    cursor: "pointer",
    fontSize: "1rem",
    fontWeight: "500",
  },
};
