import React, { useState } from "react";
import Home from "./pages/Home";
import Login from "./pages/Login";
import Register from "./pages/Register";
import UserHome from "./pages/UserHome";

function App() {
  const [page, setPage] = useState("home");
  const [userType, setUserType] = useState(null);

  // handle login success
  const handleLoginSuccess = (data, type) => {
    if (type === "user") {
      setUserType("user");
      setPage("userHome");
    } else if (type === "kiosk") {
      setUserType("kiosk");
      // later you can make kiosk dashboard
    }
  };

  // handle logout
  const handleLogout = () => {
    setUserType(null);
    setPage("home");
  };

  return (
    <>
      {page === "home" && (
        <Home
          onChoose={(type) => setPage(type === "user" ? "loginUser" : "loginKiosk")}
          onRegister={() => setPage("register")}
        />
      )}

      {page === "loginUser" && (
        <Login
          type="user"
          onBack={() => setPage("home")}
          onSuccess={(data) => handleLoginSuccess(data, "user")}
        />
      )}

      {page === "loginKiosk" && (
        <Login
          type="kiosk"
          onBack={() => setPage("home")}
          onSuccess={(data) => handleLoginSuccess(data, "kiosk")}
        />
      )}

      {page === "register" && <Register onBack={() => setPage("home")} />}

      {page === "userHome" && (
        <UserHome
          onLogout={handleLogout}
          onScan={() => alert("QR scan feature coming soon!")}
        />
      )}

    {page === "KioskHome" && (
            <KioskHome
              onLogout={handleLogout}
              onScan={() => alert("QR scan feature coming soon!")}
            />
          )}


    </>
  );
}

export default App;
